import './App.css';
import { useState } from 'react';
import OpenAI from 'openai';

function App() {
  const client = new OpenAI({
    apiKey: process.env.REACT_APP_OPENAI_API_KEY,
  });

  const [messages, setMessages] = useState([
    { role: 'assistant', content: "Hello! I'm your medical assistant. I'll help assess your symptoms and direct you to the appropriate healthcare professional. Could you please describe what brings you in today?" }
  ]);
  const [inputMessage, setInputMessage] = useState('');
  const [isDarkMode, setIsDarkMode] = useState(false);

  const handleSendMessage = async () => {
    if (inputMessage.trim() === '') return;

    setMessages([...messages, { role: 'user', content: inputMessage }]);
    setInputMessage('');

    try {
      const response = await client.chat.completions.create({
        model: "gpt-4",
        messages: [
          { role: "system", content: "You are a medical assistant helping to triage patients based on their symptoms. You will be given a patient's symptoms and you will need to determine the appropriate care level, specifically, if it should be directed to the ER, a nurse, a physician, or a physician assistant." },
          ...messages,
          { role: "user", content: inputMessage }
        ]
      });

      const assistantMessage = response.choices[0].message.content;
      setMessages(prevMessages => [...prevMessages, { role: 'assistant', content: assistantMessage }]);
    } catch (error) {
      console.error('Error calling OpenAI API:', error);
      setMessages(prevMessages => [...prevMessages, { 
        role: 'assistant', 
        content: "I'm sorry, I encountered an error. Please try again." 
      }]);
    }
  };

  return (
    <div className={`App ${isDarkMode ? 'dark-mode' : 'light-mode'}`}>
      <div className="chat-container">
        <header className="chat-header">
          <h1>Triage</h1>
          <button 
            className="theme-toggle"
            onClick={() => setIsDarkMode(!isDarkMode)}
          >
            {isDarkMode ? '‚òÄÔ∏è' : 'üåô'}
          </button>
        </header>

        <div className="messages-container">
          {messages.map((message, index) => (
            <div key={index} className={`message ${message.role}`}>
              {message.content}
            </div>
          ))}
        </div>

        <div className="input-container">
          <input
            type="text"
            value={inputMessage}
            onChange={(e) => setInputMessage(e.target.value)}
            placeholder="Describe your symptoms..."
            onKeyPress={(e) => e.key === 'Enter' && handleSendMessage()}
          />
          <button onClick={handleSendMessage}>Send</button>
        </div>
      </div>
    </div>
  );
}

export default App;
